public IList<MyObject> RefreshSeats(Int64 anId, List<int>
objectIdList) {
    try {

        return Session.CreateQuery
            (@"
            select s
            from MyObject s
            where s.anId = ?
            and s.objectId in (?)")
             .SetParameter(0, anId)
             .SetParameter(1, objectIdList)
             .SetCacheable(true)
             .SetCacheMode(CacheMode.Refresh)
             .List<MyObject>();
    }
    catch (Exception e) {
        throw e;
    }
